// local paths module

// get the app root directory
var appRootDir = require('app-root-path').path
// use this to get any file from the app root directory
	,	reqFile = require('app-root-path').require
  , path = require('path');

var paths = {}
	,	jwt_key = process.env.SECRET_JWT_KEY || 'secret'
	,	session_key = process.env.SECRET_SESSION_KEY || 'super secret hur hur'

// paths
paths.root = appRootDir;
paths.configDir = path.join(appRootDir, 'config');
paths.utilsDir = path.join(appRootDir, 'utils');
paths.modelsDir = path.join(appRootDir, 'models');
paths.controllersDir = path.join(appRootDir, 'controllers');
paths.middleware = path.join(appRootDir, 'middleware');
// files
paths.mongoose = path.join(paths.configDir, 'connection');
paths.routes = path.join(paths.utilsDir, 'combinedRoutes');
// middleware
paths.auth = path.join(paths.middleware, 'auth');
paths.jwt = path.join(paths.middleware, 'jwt');
paths.saltRounds = 10;

var getMethods = function (obj)
{
  var res = [];
  for (var m in obj) {
      if(typeof obj[m] == "function") {
          res.push(m);
      }
  }
  return res;
}


module.exports = {
	'paths': paths,
	'jwt_key': jwt_key,
	'session_key': session_key,
	'salt_rounds': paths.saltRounds,
	'getMethods': getMethods
};